import React from "react";
import { useGetTasksQuery } from "../../services/kanbanApi";

const Kanban = () => {
  const { data: tasks, error, isLoading } = useGetTasksQuery();

  if (isLoading) {
    return <div className="p-4 text-center">Loading tasks...</div>;
  }

  if (error) {
    return <div className="p-4 text-center text-error">Error loading tasks.</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Kanban Board</h1>
      <div className="grid grid-cols-4 gap-4">
        {/* Render each task category */}
        {Object.entries(tasks).map(([category, taskList]) => (
          <div key={category} className="border rounded-lg p-4 bg-gray-100">
            <h2 className="text-xl font-semibold mb-2 capitalize">
              {category}
            </h2>
            <ul>
              {taskList.map((task) => (
                <li key={task.id} className="mb-2 p-2 bg-white rounded shadow">
                  {task.title}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Kanban;
